import React, { memo } from "react";

import CardMedia from "@material-ui/core/CardMedia";

const hindi = 70;

const num = ({hindi}/100) * 17711034;

console.log(({hindi}/100) * 17711034)

import {
  ZoomableGroup,
  ComposableMap,
  Geographies,
  Geography,
  Marker
} from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const rounded = num => {
  if (num > 1000000000) {
    return Math.round(num / 100000000) / 10 + "Bn";
  } else if (num > 1000000) {
    return Math.round(num / 100000) / 10 + "M";
  } else {
    return Math.round(num / 100) / 10 + "K";
  }
};

const MapChart = ({ setTooltipContent }) => {
  return (
    <div className="main">
      <ComposableMap data-tip="" projectionConfig={{ scale: 160 }}>
        <ZoomableGroup>

          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}

                  //fill="#DDD"
                  //stroke="#FFF"

                  onMouseEnter={() => {
                    const { NAME, POP_EST } = geo.properties;
                    setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
                  }}

                  onMouseLeave={() => {
                    setTooltipContent("");
                  }}
                  style={{
                    default: {
                      fill: "#9299B8",
                      outline: "none",
                      top: '171px',
                      left: '170px'
                   },

                    hover: {
                      stroke: "#3689FD",
                      fill: "#9299B8",
                      outline: "none",                                       
                    },
                    pressed: {
                      fill: "black",
                      outline: "none"
                    },

                  }}
                />
              ))
            }
          </Geographies>
          <Marker coordinates={[240, 135]}>          
            <CardMedia 
            alt="" 
            component="img" 
            title="DataCenter"
            height="150" 
            image="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />

          </Marker>

          <Marker coordinates={[274, 146]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

          <Marker coordinates={[315, 195]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

          <Marker coordinates={[348, 163]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

          <Marker coordinates={[435, 162]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

          <Marker coordinates={[473, 156]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

          <Marker coordinates={[479, 202]}>
            <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />
            <circle r={5} fill="#F53" />
          </Marker>

        </ZoomableGroup>





      </ComposableMap>
    </div>
  );
};

export default memo(MapChart);
