import React, { memo } from "react";


import {
  ZoomableGroup,
  ComposableMap,
  Geographies,
  Geography,
  Marker
} from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const rounded = num => {
  if (num > 1000000000) {
    return Math.round(num / 100000000) / 10 + "Bn";
  } else if (num > 1000000) {
    return Math.round(num / 100000) / 10 + "M";
  } else {
    return Math.round(num / 100) / 10 + "K";
  }
};

const MapChart = ({ setTooltipContent }) => {
  return (
    <div className="main">
      <ComposableMap data-tip="" projectionConfig={{ scale: 160 }} projection="geoAlbers">
        <ZoomableGroup>

          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}

                  //fill="#DDD"
                  //stroke="#FFF"

                  onMouseEnter={() => {
                    const { NAME, POP_EST } = geo.properties;
                    setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
                  }}

                  onMouseLeave={() => {
                    setTooltipContent("");
                  }}
                  style={{
                    default: {
                      fill: "#9299B8",
                      outline: "none",
                      top: '171px',
                      left: '170px'
                   },

                    hover: {
                      stroke: "#3689FD",
                      fill: "#9299B8",
                      outline: "none",                                       
                    },
                    pressed: {
                      fill: "black",
                      outline: "none"
                    },                    
                  }}
                />
              ))
            }
          </Geographies>
        </ZoomableGroup>

        <Marker coordinates={[-74.006, 40.7128]}>
          <img alt="" src="https://res.cloudinary.com/di01osmzz/image/upload/v1638426631/Group_11920_s5w80j.png" />

        </Marker>

      </ComposableMap>
    </div>
  );
};

export default memo(MapChart);
